import React,{Component, Fragment} from 'react';
import {Link} from 'react-router-dom';
import logo from './temp.png';
import profile from './profile.png';
import './header.scss';
import {connect} from 'react-redux';


class Header extends Component {


    render(){
        let photo=null;
        if(this.props.isAuth){
            photo = this.props.currentUser.photo ? this.props.currentUser.photo : 'default';
        }
        return(
            <nav>
                <ul className="header">
                    <div className="logo">
                        <li><Link to="/"><img src={logo} alt="logo"/></Link></li>
                    </div>
                    <div>
                        {!this.props.isAuth ? 

                            <div className="menu"><li><Link to="/signup">Sign up</Link></li>
                            <li><Link to="/signin">Sign in</Link></li></div> 

                            : 

                            <div className="loginMenu">
                                <li><Link to={`/user/${this.props.currentUser._id}`}>
                                    <img src={profile} alt="profile" />
                                    </Link></li>
                                <li><Link to="/signout">Signout</Link></li>
                            </div>
                            }

                    </div>
                </ul>
            </nav>
            // <div className="header">
            //     <h2><Link to="/">Jop search Portal</Link></h2>
            //     <div className="options">
            //         <Link to="/signup">Sign Up</Link>
            //         <Link to="/signin">Sign In</Link>
            //     </div>
            // </div>
        );
    }
}

function mapStateToProps(state){
    return {
      isAuth : state.user.isAuth,
      currentUser : state.user.currentUser
    }
  }


export default connect(mapStateToProps)(Header);
